import React, { Component, useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import { withRouter } from "react-router";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import {
  BrowserRouter,
  Routes,
  Route,
  useLocation,
  useNavigate,
} from "react-router-dom";
import { Navigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

function PayUser() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [step, setStep] = useState(0);

  const handleSubmit = (event) => {
    event.preventDefault();
    let data = { USER_EMAIL: email, USER_PASSWORD: password };
    console.log(data);
    event.preventDefault();
    localStorage.setItem("user", null);
    axios
      .post("http://localhost:3001/api/v1/auth/login", data)
      .then((response) => {
        console.log(response);
        if (response.status == 200) {
          localStorage.setItem("user", JSON.stringify(response.data));
          console.log(response.status);
          navigate("/user/home");
        }
      })
      .catch((err) => {
        console.log(err);
        Swal.fire({
          title: "อีเมล์/รหัส ผ่านไม่ถูกต้อง",
          text: "โปรดกรอกอีเมล์หรือรหัสผ่านใหม่",
          icon: "error",
          confirmButtonText: "หน้าปิ",
        });
      });
  };
  return (
    <div>
      {() => {
        if (step == 0) {
          return (
            <div>
              <form onSubmit={handleSubmit}>
                <div className="input-group mb-3">
                  <span className="input-group-text" id="basic-addon1">
                    ชื่อ-นามสกุล
                  </span>
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Username"
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                <div className="input-group mb-3">
                  <span className="input-group-text" id="basic-addon1">
                    เบอร์โทรศัพท์
                  </span>
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Username"
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>

                <div className="input-group mb-3">
                  <span className="input-group-text" id="basic-addon1">
                    เลขตัวถัง
                  </span>
                  <input
                    type="text"
                    class="form-control"
                    placeholder="Username"
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </div>
                <button type="submit" className="btn btn-primary mb-3">
                  ยืนยัน
                </button>
              </form>
            </div>
          );
        } else if (step == 1) {
          return <div>dfgdgdf</div>;
        }
      }}
    </div>
  );
}

export default PayUser;
