    if (option.MOTORCYCLE_BUCKET_NUMBER !== null) {
      return <li {...props}>{option.MOTORCYCLE_BUCKET_NUMBER}</li>;
    }
    return null;
  }}
  sx={{ width: 300 }}
  freeSolo
  renderInput={(params) => <TextField {...params} label="MOTORCYCLE_BUCKET_NUMBER" />}
/>
