import React, { Component, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Outlet } from "react-router-dom";
import { withRouter } from "react-router";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";
import { Navigate } from "react-router-dom";
import { Grid, TextField, Button, Box } from "@mui/material";

function UpdateMotorcycle() {
  const { MOTORCYCLE_ID } = useParams();
  const [RegistrationNumber, setRegistrationNumber] = useState("");
  const [BucketNumber, setBucketNumber] = useState("");
  const [Brand, setBrand] = useState("");
  const [Model, setModel] = useState("");
  const [Color, setColor] = useState("");
  const [Price, setPrice] = useState("");
  const [Balance, setBalance] = useState("");
  const [image, setImage] = useState();

  useEffect(() => {
    var requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "http://localhost:3001/api/v1/motorcycles/" + MOTORCYCLE_ID,
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => {
        if (result["status"] == "ok") {
          setRegistrationNumber(
            result["result"]["MOTORCYCLE_REGISTRATION_NUMBER"]
          );
          setBucketNumber(result["result"]["MOTORCYCLE_BUCKET_NUMBER"]);
          setBrand(result["result"]["MOTORCYCLE_BRAND"]);
          setModel(result["result"]["MOTORCYCLE_MODEL"]);
          setColor(result["result"]["MOTORCYCLE_COLOR"]);
          setPrice(result["result"]["MOTORCYCLE_PRICE"]);
          setBalance(result["result"]["MOTORCYCLE_BALANCE"]);
        }
      })
      .catch((error) => console.log("error", error));
  }, [MOTORCYCLE_ID]);

  const handleSubmit = (event) => {
    event.preventDefault();
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      MOTORCYCLE_BALANCE: Balance,
      MOTORCYCLE_PRICE: Price,
      MOTORCYCLE_BRAND: Brand,
      MOTORCYCLE_MODEL: Model,
      MOTORCYCLE_COLOR: Color,
      MOTORCYCLE_REGISTRATION_NUMBER: RegistrationNumber,
      MOTORCYCLE_BUCKET_NUMBER: BucketNumber,
    });

    var requestOptions = {
      method: "PUT",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    fetch(
      `http://localhost:3001/api/v1/motorcycles/${MOTORCYCLE_ID}`,
      requestOptions
    )
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };

  const handleChangeImage = (event) => {
    const newImage = event.target.files[0];
    if (newImage) {
      setImage(URL.createObjectURL(newImage));
    }
  };

  const handleRemoceImage = (event) => {
    setImage("");
  };

  const uploadImage = async () => {
    //setLoading(true);

    //formData.append("cloud_name", process.env.REACT_APP_CLOUDINARY_CLOUD_NAME);
    //formData.append("folder", "Cloudinary-React");
    try {
      const responseBlob = await fetch(image);
      const blob = await responseBlob.blob();
      const file = new File([blob], "filename.jpg", { type: "image/jpeg" });

      const formData = new FormData();
      formData.append("file", file);
      formData.append("upload_preset", "szopv83k");

      const response = await fetch(
        `https://api.cloudinary.com/v1_1/dx59hbzcc/image/upload`,
        {
          method: "POST",
          body: formData,
        }
      );
      const res = await response.json();
      return res;
    } catch (error) {
      console.log(error);
      //setLoading(false);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <Grid item xs={12} sm={6}>
          <TextField
            id="RegistrationNumber"
            label="เลขทะเบียน"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setRegistrationNumber(e.target.value)}
            value={RegistrationNumber}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="BucketNumber"
            label="เลขตัวถัง"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setBucketNumber(e.target.value)}
            value={BucketNumber}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Brand"
            label="ยี่ห้อ"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setBrand(e.target.value)}
            value={Brand}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Model"
            label="รุ่น"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setModel(e.target.value)}
            value={Model}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Color"
            label="สี"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setColor(e.target.value)}
            value={Color}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Price"
            label="ราคา"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setPrice(e.target.value)}
            value={Price}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Balance"
            label="ยอดคงเหลือ"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setBalance(e.target.value)}
            value={Balance}
          ></TextField>
        </Grid>
        <div>
          <Box
            component="img"
            sx={{
              height: 300,
              width: 300,
              borderColor: "primary.main",
              borderRadius: "50%",
              border: 15,
            }}
            src={image}
          />

          <Button onClick={handleRemoceImage}>ล้างรูป</Button>
        </div>
        <div>
          <Button variant="contained" component="label">
            Upload File
            <input
              accept="image/*"
              type="file"
              hidden
              onChange={handleChangeImage}
            />
          </Button>
        </div>

        <button type="submit" variant="contained" class="btn btn-primary mb-3">
          แก้ไข
        </button>
      </form>
    </div>
  );
}
export default UpdateMotorcycle;
