      setTimeout(() => {
        toggleOpen(true);
        setDialogValue({
          USER_FULLNAME: newValue,
          MOTORCYCLE_BUCKET_NUMBER: "", // reset MOTORCYCLE_BUCKET_NUMBER
        });

        const selectedMotorcycleId = motorcycleIdMap.get(newValue); // ดึง MOTORCYCLE_ID จาก Map
        if (selectedMotorcycleId) {
          setSelectedMotorcycleId(selectedMotorcycleId);
        } else {
          setSelectedMotorcycleId(null);
        }
      });
