          options={items}
          getOptionLabel={(option) => {
            if (typeof option === "string") {
              return option;
            }
            if (option.inputValue) {
              return option.inputValue;
            }
            return option.USER_TELL;
          }}
          selectOnFocus
          clearOnBlur
          handleHomeEndKeys
          renderOption={(props, option) => {
            if (option.USER_TELL !== null) {
              return <li {...props}>{option.USER_TELL}</li>;
            }
            return null;
          }}
          sx={{ width: 300 }}
          freeSolo
          renderInput={(params) => <TextField {...params} label="USER_TELL" />}
        />
      </React.Fragment>
      <Grid item xs={12} sm={6}>
          <TextField
            id="Wise"
            label="อาการ"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setWise(e.target.value)}
          ></TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            id="Status"
            label="สถานะ"
            variant="outlined"
            fullWidth
            required
            onChange={(e) => setStatus(e.target.value)}
          ></TextField>
        </Grid>
        <Button onClick={handleClose}>ยกเลิก</Button>
        <button type="submit" variant="contained" class="btn btn-primary mb-3">
          บันทึก
        </button>
        </form>
      </DialogContent>
    </Dialog>
  );
}
